const scrollanime = document.getElementsByClassName("is-anime");
let scrollY;
function animationShow() {
  for (let e = 0; e < scrollanime.length; e++)
    null === scrollanime[e].getAttribute("class").match(/is-show/) &&
      ((scrollY = window.pageYOffset + window.innerHeight),
      (clientTop =
        scrollanime[e].getBoundingClientRect().top + window.pageYOffset),
      clientTop < scrollY &&
        scrollanime[e].setAttribute(
          "class",
          scrollanime[e].getAttribute("class") + " is-show"
        ));
}
animationShow(),
  window.addEventListener("scroll", () => {
    animationShow();
  });
const mediaQuery = window.matchMedia("(max-width: 1000px)"),
  target = document.querySelector(".entry-btn");
function handle(e) {
  e.matches
    ? (target.classList.add("entry-sp"), target.classList.remove("entry-pc"))
    : (target.classList.add("entry-pc"), target.classList.remove("entry-sp"));
}
handle(mediaQuery),
  mediaQuery.addListener(handle),
  window.addEventListener("scroll", function () {
    const e = document.querySelector(".entry-sp"),
      t = document.body.clientHeight,
      o =
        document.documentElement.clientHeight +
          document.documentElement.scrollTop ||
        document.body.scrollTop ||
        document.scrollingElement.scrollTop ||
        window.pageYOffset ||
        window.scrollY,
      n = document.querySelector(".footer").clientHeight;
    window.matchMedia("(max-width: 1000px)").matches &&
      (t - o <= n
        ? ((e.style.position = "absolute"), (e.style.bottom = n + 1 + "px"))
        : ((e.style.position = "fixed"), (e.style.bottom = "0")));
  });
const nav = document.getElementById("navMenu"),
  ham = document.getElementById("navBtn"),
  navlist = document.getElementById("navList");
ham.addEventListener("click", function () {
  nav.classList.toggle("nav-open"), ham.classList.toggle("nav-open");
}),
  navlist.addEventListener("click", function () {
    nav.classList.remove("nav-open"), ham.classList.remove("nav-open");
  });
const pagetopBtn = document.querySelector(".totop");
pagetopBtn.addEventListener("click", () => {
  window.scrollTo({ top: 0, behavior: "smooth" });
});
